      document.getElementById("divAlertaErro").style.display = "block";
      document.getElementById("AlertaErroInf").innerHTML = "Mensagem";
  }

  return(
    <div>
      <h2>moAlerta</h2>
      <a onClick={() => {enviar()}}>Abra aqui</a>
      <Alerta modo={modo}/>      
