import React from 'react'
import Img from '../../atoms/atImg/index.js';
import Div from '../../atoms/atDiv'
import SelectOption from './selectOption.js'

import * as Util from '../../../controller/util.js';
//console.log('moHeaderSideDoValente ');

const moHeaderSideDoValente = function (props) {
  const regiao = props.regiao;
  const modo = props.modo;
  const JsonRegiao = Util.regiao(regiao);
  console.log("regiao="+regiao);
  console.log("modo="+modo);
  console.log(JsonRegiao);
  console.log('╔════════════════════════════════╗');
  console.log(JsonRegiao.estado);
  console.log(JsonRegiao.capital);
  console.log('╚════════════════════════════════╝');

  const listaRegioes = Util.listaRegioes();

  return (
    <Div display="justificado">
        <div className="DivHeaderSideDoValente">
          <a href="/">
            <Img src="http://localhost:8000/static/assetsv5/img/log_laranja1.png" width="50px" height="50px" alt="Logo Site Valente" title="Logo Site Valente" class="LogoSiteValente" modo={modo} />
          </a>
        </div>
        <div className='DivHeaderSideDoValente'>
          <span className='pHeaderSideDoValente'>Site do Valente</span>
        </div>
        <div className='DivHeaderSideDoValente'>
          <span>
            Cidade : {JsonRegiao.capital}<br/>
            Estado : {JsonRegiao.sigla+" - "+JsonRegiao.estado}<br/>
            Modo : {modo}<br/>
            Região :
            <select className="form-control">
              {  listaRegioes.map((item, i) => {
                if(item.sigla==JsonRegiao.sigla){
                  return (<option value={item.sigla} select>{item.sigla+'-'+item.estado}</option>)
                }else{
                  return (<option value={item.sigla}>{item.sigla+'-'+item.estado}</option>)
                }
              })}
            </select>
          </span>
        </div>
      <style jsx>{`
      .DivHeaderSideDoValente{
        margin:0px;
        padding: 0px;

      }
      .pHeaderSideDoValente{
        font-size: 40px;
        font-family: sans-serif;
        text-align: center;
      }
    `}</style>
    </Div>
  )
}

export default moHeaderSideDoValente
