import React from 'react'
import Head from 'next/head'
import FaviIcon from '../../atoms/atFavIcon'
import Layout from '../../moleculas/layout'
import * as Util from '../../../controller/util.js';

const atendimentoOi = function (props) {

  console.log('╔════════════════════════════════╗');
  console.log('║        codigo   PHP       ║');
  console.log('╚════════════════════════════════╝');

  var cidade = Util.validaProps(props.cidade, "Rio de Janeiro");
  var modo = Util.validaProps(props.modo, "html");


  return (
    <div>
      <Head>
        <title>CRIANDO REDIRECIONAMENTOS NO PHP</title>
        <FaviIcon />
        <meta name="description" content="PHP" />
        <link rel="canonical" href="/codigo/PHP" />
        <link rel="amphtml" href="/amp/codigo/PHP" />
        <script async custom-element="amp-lightbox" src="https://cdn.ampproject.org/v0/amp-lightbox-0.1.js"></script>
        <script async custom-element="amp-iframe" src="https://cdn.ampproject.org/v0/amp-iframe-0.1.js"></script>
      </Head>
      <Layout cidade={cidade} modo={modo}>
          <h1>CRIANDO REDIRECIONAMENTOS NO PHP</h1>
          <p>{'<?php'}<br/>
          Header( "HTTP/1.1 301 Moved Permanently" );<br/>
          Header( "Location: http://www.seu-novo-site.com.br" );<br/>
          {'?>'}<br/>
          </p>
      </Layout>
      <style jsx>{`
        .h1{
          color : #000000;
        }
        .p{
          color : #000000;
        }
      `}</style>
    </div>
  )
}

export default atendimentoOi
