import React from 'react'
import Div from '../../atoms/atDiv'

import ListadeRotas from '../moListadeRotas/index.js'
import Janela from '../moJanela/index.js'
import HeaderSideDoValente from '../moHeaderSideDoValente/index.js'
import * as Util from '../../../controller/util.js';

const layout = props => {
  console.log('Rodando /layout.js Luz Verde');
  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");
  var titulo = Util.validaProps(props.titulo, "Home");
  var categoria = Util.validaProps(props.categoria, "Outros");

  //Layout Luz verde
  return (
    <div className="Body">
      <div className="borda"></div>
      <Div display="justificado">
        <div className="borda"></div>
        <Div display="justificado">
          <div className="codigoIndexListadeRotas esconder">
            <Janela estilo="luz">
              <ListadeRotas regiao={regiao} modo={modo} titulo={titulo} categoria={categoria}/>
            </Janela>
          </div>

          <div className="borda esconder"></div>

            <Div display="vertical">

              <Janela estilo="luz">
                <HeaderSideDoValente regiao={regiao} modo={modo}/>
              </Janela>

              <div className="borda"></div>


                <Janela estilo="luz">
                  {props.children}
                </Janela>


              <div className="borda"></div>

              <Janela estilo="luz">
                <HeaderSideDoValente regiao={regiao} modo={modo}/>
              </Janela>

            </Div>      

          </Div>


        <div className="borda"></div>

      </Div>
      <style jsx>{`
      .atDivJustificado {
        display: flex;
        justify-content: space-between;
        width: 100%;
      }
      .Body{
        color: #ffffff;
        width:100%;
        margin : 0px;
        padding : 0px;
      }
      .borda{
        width:0px;
        height: 0px;
      }

      /* .menu { width:600px; } */
      .codigoIndexListadeRotas{
        width: 25%;
      }

      .divCss{
          width:100%;
          padding-left:20px;
        }

      @media(max-width: 1169px) {
        .esconder{
          display : None;
        }

      }

    `}</style>
      <style jsx global>{`
        ${Util.fontesCSS1()}
        ${Util.fontesCSS2()}
        ${Util.fontesCSS3()}
        ${Util.fontesCSS4()}
        ${Util.fontesCSS5()}
        body{
          margin: 0px;
          padding: 0px;
          background-color: #002128;
          background-image: url("https://i.pinimg.com/564x/e9/e5/5d/e9e55d996883693f20d56f70964bb6a9.jpg");

          color: #10ecf5;
          font-family: SimplonBP-Regular, sans-serif;       

        }
    `}</style>
    </div>
  )
}

export default layout
