import React from 'react'
//import Link from 'next/link'
import Head from 'next/head'
import FaviIcon from '../../atoms/atFavIcon'
import Layout from '../../moleculas/layout'
import * as Util from '../../../controller/util.js';

const atendimentoOi = function (props) {

  console.log('╔════════════════════════════════════════╗');
  console.log('║      GAME    controleogodzilla         ║');
  console.log('╚════════════════════════════════════════╝');

  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");

	// if (document.all){}
	// else document.captureEvents(Event.MOUSEMOVE);
	// document.onmousemove=mouse;

	// function mouse(e){
	// 	if (navigator.appName == 'Netscape'){
	// 		xcurs = e.pageX;
	// 		ycurs = e.pageY;
	// 	} else {
	// 		xcurs = event.clientX;
	// 		ycurs = event.clientY;
	// 	}
	// 	document.getElementById('position').style.left = (xcurs-300)+'px';
	// 	document.getElementById('position').style.top = (ycurs-300)+'px';
	// }

  function AlteraPersonagem(){
    //document.getElementById("AlertaErroInf").innerHTML = "";  
    //document.getElementById("divAlertaErro").style.display = "none";

	  //document.getElementById('mostro').style.display= "none"; 

    if(document.getElementById('mostro').src=='https://media1.giphy.com/media/11HglJPbu1w2GY/source.gif'){
			document.getElementById('mostro').src = 'https://cdn.lowgif.com/full/05e38ab6f139bb7e-my-godzilla-gifs-tumblr.gif'; 
		}else{
			document.getElementById('mostro').src = 'https://media1.giphy.com/media/11HglJPbu1w2GY/source.gif'; 
		}
  }

	// function myFunction(mostro) {
	// 	if(document.getElementById('mostro').src=='https://media1.giphy.com/media/11HglJPbu1w2GY/source.gif'){
	// 		document.getElementById('mostro').src = 'https://cdn.lowgif.com/full/05e38ab6f139bb7e-my-godzilla-gifs-tumblr.gif'; 
	// 	}else{
	// 		document.getElementById('mostro').src = 'https://media1.giphy.com/media/11HglJPbu1w2GY/source.gif'; 
	// 	}
	// }

  return (
    <div>
      <Head>
        <title>Controle o Godzilla</title>
        <FaviIcon />
        <meta name="description" content="Controle o Godzilla" />
        <link rel="canonical" href="/game/controleogodzilla" />
        <link rel="amphtml" href="/amp/game/controleogodzilla" />
        <script async custom-element="amp-lightbox" src="https://cdn.ampproject.org/v0/amp-lightbox-0.1.js"></script>
        <script async custom-element="amp-iframe" src="https://cdn.ampproject.org/v0/amp-iframe-0.1.js"></script>
      </Head>
      <Layout regiao={regiao} modo={modo}>
          <div className="Godzilla" >
            <a onClick={() => {AlteraPersonagem()}}>
              <img 
                id="mostro" 
                src="https://cdn.lowgif.com/full/05e38ab6f139bb7e-my-godzilla-gifs-tumblr.gif" 
                width="500" 
                height="379"/>
	          </a>
          </div>
          <h1>GAME</h1>
          <p>Brincadeira que fiz pro meu filho.</p>
          <img 
              alt="Fundo" 
              src="https://img-s-msn-com.akamaized.net/tenant/amp/entityid/BBVShEa.img?h=416&amp;w=799&amp;m=6&amp;q=60&amp;u=t&amp;o=f&amp;l=f&amp;x=871&amp;y=654" 
              width="100%" 
              height="100%" 
              title="Cenário de cidades"/>
      </Layout>
      <style jsx>{`
        .Godzilla {
          position: absolute;
        }
      `}</style>

    </div>
  )
}

