import React from "react";
import LoginMinhaOi from '../moLoginMinhaOi/index.js';
import Link from "../../../atoms/atLink";
import * as Util from '../../../../controller/util.js';

const headerEntrar = props => {
    var modo = Util.validaProps(props.modo,"html");

    return (
        <div className="headerEntrar">
            <div className="divHeaderCadastrese">
                <Link href="https://minha.oi.com.br/minhaoi/cadastro/#/cadastro/cpf/" value="Cadastre-se agora" estilo="LinkHeaderMinhaOi"/>
            </div>
            <div className="divHeaderCadastreseMobile">
                <Link href="https://minha.oi.com.br/minhaoi/cadastro/#/cadastro/cpf/" value="Cadastrar" estilo="LinkHeaderMinhaOi"/>
            </div>
            <div className="divHeaderEntrar">
                <LoginMinhaOi modo={modo}/>
            </div>
            <style jsx>{`
                .headerEntrar{
                    display: inline-flex;
                    flex-wrap: wrap;
                    font-family: SimplonBP-Regular;
                }
                .divHeaderEntrar{

                }
                .divHeaderCadastrese{
                    margin-right: 30px;
                }
                .divHeaderCadastreseMobile{
                    display: none;
                }
                @media only screen and (max-width : 1000px) {
                    .divHeaderCadastreseMobile{
                        font-size: 0.875rem; /*14px*/
                        display:block;
                        margin-right: 20px;
                    }
                    .divHeaderCadastrese{
                        display: none;
                    }
                }
            `}</style>
        </div>
    )
}

export default headerEntrar


