import React from 'react'
import Div from '../../atoms/atDiv'

import ListadeRotas from '../moListadeRotas/index.js'
import Janela from '../moJanela/index.js'
import HeaderSideDoValente from '../moHeaderSideDoValente/index.js'
import FooterSideDoValente from '../mofooterSideDoValente/index.js'
import * as Util from '../../../controller/util.js';
import GTM from '../../atoms/atGTM';

const layout = props => {
  console.log('Rodando /layout.js Luz Verde');
  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");
  var titulo = Util.validaProps(props.titulo, "Home");
  var categoria = Util.validaProps(props.categoria, "Outros");

  //Layout Luz verde
  return (
    <div className="CSSLayout">
      <GTM position="body" modo={modo} codGTM="UA-143307429-1" />
      <div className="HeaderEu centraliza"> 
        <div className="HeaderEu2 tamanhoPagina">
          Header eu
        </div>
      </div>
      <div className="centraliza">
      <div className="tamanhoPagina">
      <Div display="vertical">
        <Div display="justificado">
          <div className="layoutBody">
              <Janela estilo="claro">
                <div className="fundoEu">fundo</div>
                <div className="conteudo">
                {props.children}
                </div>
              </Janela>
          </div>      
          <div className="layoutManu esconder">
            <Janela estilo="claro">
                <ListadeRotas regiao={regiao} modo={modo} titulo={titulo} categoria={categoria}/>
            </Janela>
          </div>
        </Div>
        <Janela estilo="claro">
                <FooterSideDoValente regiao={regiao} modo={modo}/>
        </Janela>
        </Div>
      </div>
      </div>
      <style jsx>{`
      .centraliza{
        align-items: center;
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: center;

      }
      .tamanhoPagina{
        width:100%;
        max-width : 1128px;
        margin : 0px;
        padding : 0px;
      }
      .HeaderEu{
        color: #ffffff;
        background-color: #283e4a;
        width:100%;
        margin : 0px;
      }
      .HeaderEu2{
        min-height: 100%;
      }
      .CSSLayout{
        color: #ffffff;
        width:100%;
        margin : 0px;
        padding : 0px;
      }
      .layoutManu{
        margin-top: 40px;
        width: 25%;
        /*padding-left:2%;*/
      }
      .layoutBody{
        width: 71%;
        margin-top: 40px;
        padding-bottom:10px;
      }
      @media(max-width: 1169px) {
        .layoutManu{
          display : None;
        }
        .layoutBody{
          width: 100%;
          margin-top: 5px;
        }
      }
    `}</style>
      <style jsx global>{`
        ${Util.fontesCSS1()}
        body{
          margin: 0px;
          padding: 0px;
          background-color: #f5f5f5;
          color: #000000;
          font-family: SimplonBP-Regular, sans-serif;       
        }
    `}</style>
    </div>
  )
}

export default layout

