import React from 'react'
import Layout from '../../../moleculas/layout'
import MoHead from '../../../moleculas/moHead'
import DivCodigo from '../../../atoms/atDivCodigo'
import DivNaveg from '../../../atoms/atDivNaveg'
import * as Util from '../../../../controller/util.js';

const atendimentoOi = function (props) {

  console.log('╔══════════════════════════════════════╗');
  console.log('║  codigo  - HTML - Meter e Progress   ║');
  console.log('╚══════════════════════════════════════╝');

  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");

  return (
    <div>
      <MoHead modo={modo} title="HTML Meter e Progress" description="Codigo HTML Meter e Progress" canonical="/codigo/html/meter_progress/"/>
      <Layout regiao={regiao} modo={modo} titulo="Codigo" categoria="html">
        <h1>HTML Meter e Progress</h1>
        <p>
        A tag meter define uma medida escalar dentro de um intervalo conhecido ou de um valor fracionário. Isso também é conhecido como medidor.<br/>
        Exemplos: uso do disco, relevância de um resultado da consulta etc.<br/>
        Nota: A tag meter não deve ser usada para indicar progresso (como em uma barra de progresso). Para barras de progresso, use a tag progress.<br/>
        Dica: sempre adicione a tag label para obter as melhores práticas de acessibilidade!<br/>
        </p>
          Medidor (Meter) : <br/>        
        <DivNaveg>
          <meter min="0" optimum="6" high="8" max="10" value="2" ></meter><br />
          <meter min="0" optimum="5" high="8" max="10" value="6" ></meter><br />
          <meter min="0" optimum="6" high="8" max="10" value="9" ></meter><br />
          Barra de Progresso (Progress) : <br />
          <progress max="100" value="80"></progress>
        </DivNaveg>
        <DivCodigo titulo="codigo">
          {'<meter min="0" optimum="6" high="8" max="10" value="2" ></meter><br />'}<br/>
          {'<meter min="0" optimum="5" high="8" max="10" value="6" ></meter><br />'}<br/>
          {'<meter min="0" optimum="6" high="8" max="10" value="9" ></meter><br />'}<br/>
          {'Barra de Progresso (Progress) : <br />'}<br/>
          {'<progress max="100" value="80"></progress>'}<br/>
        </DivCodigo>
      </Layout>
      <style jsx>{`
        .h1{
          color : #000000;
        }
        .p{
          color : #000000;
        }
      `}</style>
    </div>
  )
}

export default atendimentoOi
