import React from 'react'
//import Link from 'next/link'
//import Head from 'next/head'
//import FaviIcon from '../../atoms/atFavIcon'
import Layout from '../../moleculas/layout'
import MoHead from '../../moleculas/moHead'
import Tanque from '../../organisms/orgTanque'
import * as Util from '../../../controller/util.js';
import BtLink from '../../atoms/atBtLink'
import Head from 'next/head'
const tanque = function (props) {

  console.log('╔════════════════════════════════════════╗');
  console.log('║  Template - GAME - Tanque de Guerra    ║');
  console.log('╚════════════════════════════════════════╝');

  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");   

  function retornaogame(modo,regiao){
    if (modo=="html"){ 
      return (<Tanque/>) 
    }else{ 
      return (
        <p>O jogo não pode ser visto no modo AMP.<br/>
          <a href={"/game/tanque/?regiao="+regiao}>Clique aqui para sair do modo AMP.</a>
          <BtLink href={"/game/tanque?regiao="+regiao} value="Clique aqui para sair do modo AMP." estilo="cinza"/><br/>
        </p>
        )
    }
  }

  return (
    <div>
      <MoHead modo={modo} title="Teste Game Tanque PhaserJS" description="Desenvolvimento de Game com PhaserJS - Tanque de Guerra." canonical="/game/tanque/"/>
      <Head>
        <script src="/static/assetsv5/js/phaser.min.js"></script>
      </Head>
      <Layout regiao={regiao} modo={modo}>
          <h1>Teste Game Tanque PhaserJS</h1>
          <p>Estudo de desenvolvimento de game com PhaserJS.</p>
          {retornaogame(modo,regiao)}
          <br/>
          S=Andar para traz<br/>
          W=Andar para frente<br/>
          A=Virar para a esquerda<br/>
          D=Virar para a direita<br/>
      </Layout>
    </div>
  )
}

