import React from 'react'
//import Link from 'next/link'
import Head from 'next/head'
import FaviIcon from '../../atoms/atFavIcon'
import Layout from '../../moleculas/layout'
import * as Util from '../../../controller/util.js';
import Div from '../../atoms/atDiv'

const conversordetexto = function (props) {

  console.log('╔═══════════════════════════════╗');
  console.log('║      conversordetexto         ║');
  console.log('╚═══════════════════════════════╝');

  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");

  function FuncConverter(varOpt){
    var saida = "opt=" + varOpt;
    var textooriginal = document.getElementById('textareaoriginal').value;
    if(varOpt=="a"){
      saida = textooriginal.toUpperCase()
      //saida = textooriginal.toLowerCase().replace(/(?:^|\s)\S/g, function(a) { return a.toUpperCase(); });
    } else if(varOpt=="b"){
      saida = textooriginal.toLowerCase().replace(/(?:^|\s)\S/g, function(a) { return a.toUpperCase(); });
    } else if(varOpt=="c"){
      saida = textooriginal.toUpperCase()
    } else if(varOpt=="e"){
      saida = textooriginal.toLowerCase()
    } 
    document.getElementById('textareaconvertido').value=saida;
    alert(saida);
  }

  function titleize(text) {
    var loweredText = text.toLowerCase();
    var words = loweredText.split(" ");
    for (var a = 0; a < words.length; a++) {
        var w = words[a];

        var firstLetter = w[0];
        w = firstLetter.toUpperCase() + w.slice(1);

        words[a] = w;
    }
    return words.join(" ");
}

  return (
    <div>
      <Head>
        <title>Conversor de Texto</title>
        <FaviIcon />
        <meta name="description" content="Conversor de Texto" />
        <link rel="canonical" href="/game/conversordetexto/" />
        <link rel="amphtml" href="/amp/game/conversordetexto/" />
        <script async custom-element="amp-lightbox" src="https://cdn.ampproject.org/v0/amp-lightbox-0.1.js"></script>
        <script async custom-element="amp-iframe" src="https://cdn.ampproject.org/v0/amp-iframe-0.1.js"></script>
      </Head>
      <Layout regiao={regiao} modo={modo}>
        <h1>Conversor de Texto</h1>
        <Div display="responsivo">
          <div>
            Digite ou cole o texto aqui:<br/>
            <textarea id="textareaoriginal" name="textareaoriginal" rows="5" cols="30" spellcheck="false"></textarea><br />
          </div>
          <div>
            Texto convertido:<br/>
            <textarea id="textareaconvertido" name="textareaconvertido" rows="5" cols="30" spellcheck="false"></textarea><br />
          </div>
        </Div>
        <Div display="responsivo">
          <div><a id="btLinkConverta" className="btLinkBranco" onClick={() => {FuncConverter("a")}}>Primeira letra da fraze maiuscula.</a></div>
          <div><a id="btLinkConvertb" className="btLinkBranco" onClick={() => {FuncConverter("b")}}>Todas As Primeiras Letras Maiusculas.</a></div>
          <div><a id="btLinkConvertc" className="btLinkBranco" onClick={() => {FuncConverter("c")}}>TUDO MAIUSCULA.</a></div>
          <div><a id="btLinkConvertd" className="btLinkBranco" onClick={() => {FuncConverter("d")}}>tudo minuscula.</a></div>
          <div><a id="btLinkConvertb" className="btLinkBranco" onClick={() => {FuncConverter("e")}}>tODAS aS pRIMEIRAS lETRAS mINUSCULAS.</a></div>
        </Div>
      </Layout>
      <style jsx>{`
        .Godzilla {
          position: absolute;
        }
      `}</style>

    </div>
  )
}

export default conversordetexto
