    }else if(tipo=="coco"){
      return {
        "tipo":"coco",
        "hierarquia":0,
        "width": 10,
        "height": 10,
        "h":100,
        "x":varPositX,
        "y":varPositY,
        "velocidade":3,
        "img":"https://image.flaticon.com/icons/svg/1642/1642901.svg"
      }  
    }
  } 

  function funcLocalInit(){
  return 100;
  }

  function iniciarGame(){

    var CNV = document.getElementById('canvasGame')
    var CTX = CNV.getContext("2d");
    var i = 0;

    var img01 = new Image();
    img01.src = "/static/assetsv5/img/carregando.png";
    //CTX.drawImage(img, 10,10)
    CTX.drawImage(img01, personagens[0].x,personagens[0].y);
