import React from 'react'
import * as Util from '../../../controller/util.js';

const headerGTM = props => {
    //var codGTM = Util.verificaValor(props.codGTM, 'UA-143307429-1');
    var modo = Util.validaProps(props.modo, 'html');
    if(modo=="html" || modo=="HTML"){
      return(
        <script async src="https://www.googletagmanager.com/gtag/js?id=UA-143307429-1"></script>
      )
    }else{
      return(
        <script async custom-element="amp-form" src="https://cdn.ampproject.org/v0/amp-form-0.1.js"></script>
      )
    }
}

export default headerGTM

