import React from 'react';
import JanelaEscuro from './JanelaEscuro.js';
import JanelaLuz from './JanelaLuz.js';
import JanelaNeon from './JanelaNeon.js';
import JanelaNaveg from './JanelaNaveg.js';
import JanelaTerminal from './JanelaTerminal.js';
import * as Util from '../../../controller/util.js';

var moJanela = function moJanela(props) {
  //console.log('Rodando /moJanela');
  var varEstilo = Util.validaProps(props.estilo, "");

  if (varEstilo == "luz") {
    //Layout Luz verde
    return React.createElement(JanelaLuz, null, props.children);
  } else if (varEstilo == "neon") {
    //Layout escuro
    return React.createElement(JanelaNeon, null, props.children);
  } else if (varEstilo == "escuro") {
    //Layout escuro
    return React.createElement(JanelaEscuro, null, props.children);
  } else if (varEstilo == "naveg") {
    //Navegador
    return React.createElement(JanelaNaveg, null, props.children);
  } else if (varEstilo == "terminal") {
    //Navegador
    return React.createElement(JanelaTerminal, null, props.children);
  } else {
    //Layout escuro
    return React.createElement("div", null, props.children);
  }
};

export default moJanela;
