import React from 'react'
import Layout from '../../moleculas/layout'
import MoHead from '../../moleculas/moHead'
import * as Util from '../../../controller/util.js';

const atendimentoOi = function (props) {

  console.log('╔════════════════════════════════╗');
  console.log('║     codigo   Ruby On Rails     ║');
  console.log('╚════════════════════════════════╝');

  var regiao = Util.validaProps(props.regiao , "RJ");
  var modo = Util.validaProps(props.modo, "html");


  return (
    <div>
      <MoHead modo={modo} title="Ruby On Rails" description="Código Ruby On Rails" canonical="/codigo/rubyonrails/"/>
      <Layout regiao={regiao} modo={modo}>
          <h1>redirect 301 em Ruby on Rails</h1>
          <p>
          def old_action<br/>
          headers["Status"] = "301 Moved Permanently"<br/>
          redirect_to "http://www.seu-novo-site.com.br/"<br/>
          End
          </p>
      </Layout>
      <style jsx>{`
        .h1{
          color : #000000;
        }
        .p{
          color : #000000;
        }
      `}</style>
    </div>
  )
}

export default atendimentoOi
